function updateSVG(instrumentValue){
// ========================================
// STUDENT EDIT ZONE
// This sections controls how the gauge displays
// ========================================

/**
 * Updates the oxygen gauge display
 * - The gauge needle rotates to show oxygen level
 * - 0% = -60 degrees (pointing left)
 * - 50% = 0 degrees (pointing up)
 * - 100% = +60 degrees (pointing right)
 */

  // Calculate rotation angle
  // Formula: (percentage - 50) * 1.2 to map 0-100 to -60 to +60 degrees
  const angle = (instrumentValue - 50) * 1.2;

  // Get the needle line element
  const line = document.getElementById('line');
  if (!line) {
    console.log('Cant find element to animate');
    return;
  }

  // Get the pivot point (start of the line)
  const x1 = parseFloat(line.getAttribute('x1'));
  const y1 = parseFloat(line.getAttribute('y1'));

  // Rotate the needle around its pivot point
  line.setAttribute('transform', `rotate(${angle}, ${x1}, ${y1})`);


// ========================================
// END STUDENT EDIT ZONE
// ========================================

}


setInterval(() => { // do this repeatedly

// Check localStorage for updates
  const thisSVGfilename = window.location.pathname;
// this SVG expects a localStorage with the same name as its file
  const localStorageName =  thisSVGfilename.substring(thisSVGfilename.lastIndexOf('/') + 1, thisSVGfilename.lastIndexOf('.'));
  let instrumentValue = localStorage.getItem(localStorageName);



if (instrumentValue !== null) {
  // Make sure percentage is between 0 and 100
  instrumentValue = Math.max(0, Math.min(100, instrumentValue));

} else {
// no localStorage, so make up a number, so it swings back and forth
// Initialize static variables if they don't exist
if (typeof window.testValue === 'undefined') {
  window.testValue = 0;
  window.testDirection = 1; // 1 for increasing, -1 for decreasing
}

// Update the test value
window.testValue += window.testDirection * 2; // increment by 2 each time

// Reverse direction at boundaries
if (window.testValue >= 100) {
  window.testValue = 100;
  window.testDirection = -1;
} else if (window.testValue <= 0) {
  window.testValue = 0;
  window.testDirection = 1;
}

instrumentValue = window.testValue;
//console.log('no local storage \"'+localStorageName+'\", using test value:', instrumentValue);

 }

updateSVG(instrumentValue)



}, 50);  //end of setInterval code.  50 is 50 milliseconds or 20 times per second.

(function() {
// Wait for DOM to be ready
      function init() {
        const fullscreenIcons = document.querySelectorAll(".fullscreen-icon");
        // Get the root SVG element (the document element when SVG is standalone)
        const svg = document.documentElement.tagName === "svg" ? document.documentElement : document.querySelector("svg");

        if (fullscreenIcons.length > 0 && svg) {
        // Add click handler to each fullscreen icon
        fullscreenIcons.forEach((icon) => {
          icon.addEventListener("click", function (e) {
            // Prevent the click from bubbling to the SVG's click handler
            e.stopPropagation();

            if (!document.fullscreenElement) {
              // Enter fullscreen
              svg.requestFullscreen().catch((err) => {
                console.error("Error attempting to enable fullscreen:", err);
              });
            } else {
              // Exit fullscreen
              document.exitFullscreen();
            }
          });
        });

        // Show/hide buttons based on fullscreen state
        document.addEventListener("fullscreenchange", function () {
          fullscreenIcons.forEach((icon) => {
            if (document.fullscreenElement) {
              icon.style.visibility = "hidden";
            } else {
              icon.style.visibility = "visible";
            }
          });
        });
      }

      // Helper function to navigate slides
      function navigateSlides(direction) {
        const slides = document.querySelectorAll(".slides > .basicSlide, .slides > .titleSlide");

        // Find currently visible slide
        let currentIndex = -1;
        for (let i = 0; i < slides.length; i++) {
          if (slides[i].classList.contains("active")) {
            currentIndex = i;
            break;
          }
        }

        let nextIndex = currentIndex;

        if (direction === "next" && currentIndex < slides.length - 1) {
          nextIndex = currentIndex + 1;
        } else if (direction === "prev" && currentIndex > 0) {
          nextIndex = currentIndex - 1;
        }

        // Switch slides if index changed
        if (nextIndex !== currentIndex && nextIndex >= 0) {
          slides[currentIndex].classList.remove("active");
          slides[nextIndex].classList.add("active");
        }
      }

      // Keyboard navigation for slides
      document.addEventListener("keydown", function (e) {
        if (e.key === "ArrowRight" || e.key === "ArrowDown" || e.key === "PageDown") {
          navigateSlides("next");
        } else if (e.key === "ArrowLeft" || e.key === "ArrowUp" || e.key === "PageUp") {
          navigateSlides("prev");
        }
      });

      // Touch navigation for mobile
      if (svg) {
        svg.addEventListener("click", function (e) {
          const svgRect = svg.getBoundingClientRect();
          const clickX = e.clientX - svgRect.left;
          const svgWidth = svgRect.width;
          const leftThird = svgWidth / 3;
          const rightThird = (svgWidth * 2) / 3;

          if (clickX < leftThird) {
            navigateSlides("prev");
          } else if (clickX > rightThird) {
            navigateSlides("next");
          }
        });

        // Update cursor based on mouse position
        svg.addEventListener("mousemove", function (e) {
          const svgRect = svg.getBoundingClientRect();
          const clickX = e.clientX - svgRect.left;
          const svgWidth = svgRect.width;
          const leftThird = svgWidth / 3;
          const rightThird = (svgWidth * 2) / 3;

          if (clickX < leftThird) {
            svg.style.cursor = "w-resize";
          } else if (clickX > rightThird) {
            svg.style.cursor = "e-resize";
          } else {
            svg.style.cursor = "default";
          }
        });
      }
      }

      // Initialize when DOM is ready
      if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', init);
      } else {
        // DOM is already ready
        init();
      }
})();
